import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addSupplier, clearErrors } from '../actions/supplierActions';
import { CREATE_SUPPLIER_SUCCESS, CREATE_SUPPLIER_FAIL } from '../constants/supplierConstants';
import { useHistory } from 'react-router-dom';

const NewSupplier = () => {
  const dispatch = useDispatch();
  const history = useHistory();
  
  // Local state để quản lý các input trong form
  const [name, setName] = useState('');
  const [contact, setContact] = useState('');
  const [address, setAddress] = useState('');
  const [email, setEmail] = useState('');
  
  // Lấy thông tin lỗi từ Redux (nếu có)
  const { error, isCreated } = useSelector(state => state.suppliers);
  
  // Xử lý khi form gửi đi
  const submitHandler = (e) => {
    e.preventDefault();

    const supplierData = {
      name,
      contact,
      address,
      email
    };

    dispatch(addSupplier(supplierData));  // Gọi action addSupplier
  };

  // Nếu tạo thành công, điều hướng về danh sách nhà cung cấp
  useEffect(() => {
    if (isCreated) {
      history.push('/admin/suppliers');  // Điều hướng về trang danh sách nhà cung cấp
    }

    if (error) {
      // Xử lý lỗi nếu có
      dispatch(clearErrors());
    }
  }, [dispatch, isCreated, error, history]);

  return (
    <div className="container">
      <h2>Thêm Nhà Cung Cấp Mới</h2>

      <form onSubmit={submitHandler}>
        <div className="form-group">
          <label>Tên Nhà Cung Cấp</label>
          <input
            type="text"
            className="form-control"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label>Liên Hệ</label>
          <input
            type="text"
            className="form-control"
            value={contact}
            onChange={(e) => setContact(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label>Địa Chỉ</label>
          <input
            type="text"
            className="form-control"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          />
        </div>

        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            className="form-control"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>

        {error && <div className="alert alert-danger mt-3">{error}</div>}

        <button type="submit" className="btn btn-primary mt-3">
          Thêm Mới
        </button>
      </form>
    </div>
  );
};

export default NewSupplier;
