import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSuppliers, removeSupplier } from '../../actions/supplierActions';

const SupplierList = () => {
    const dispatch = useDispatch();
    const { suppliers, loading, error } = useSelector((state) => state.suppliers);

    useEffect(() => {
        dispatch(fetchSuppliers());
    }, [dispatch]);

    const handleDelete = (id) => {
        if (window.confirm('Bạn có chắc chắn muốn xóa nhà cung cấp này?')) {
            dispatch(removeSupplier(id));
        }
    };

    if (loading) return <p>Đang tải...</p>;
    if (error) return <p>Lỗi: {error}</p>;

    // Kiểm tra suppliers có phải là mảng trước khi sử dụng map
    if (!Array.isArray(suppliers) || suppliers.length === 0) {
        return <p>Không có nhà cung cấp nào.</p>;
    }

    return (
        <div>
            <h1>Danh sách nhà cung cấp</h1>
            <table>
                <thead>
                    <tr>
                        <th>Tên</th>
                        <th>Email</th>
                        <th>Điện thoại</th>
                        <th>Địa chỉ</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {suppliers.map((supplier) => (
                        <tr key={supplier._id}>
                            <td>{supplier.name}</td>
                            <td>{supplier.contact?.email}</td>
                            <td>{supplier.contact?.phone}</td>
                            <td>{`${supplier.address?.street}, ${supplier.address?.city}, ${supplier.address?.state}`}</td>
                            <td>
                                <button onClick={() => handleDelete(supplier._id)}>Xóa</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default SupplierList;
