import React, { Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { MDBDataTable } from 'mdbreact';

import MetaData from '../layout/MetaData';
import Loader from '../layout/Loader';
import Sidebar from './Sidebar';

import { useAlert } from 'react-alert';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSuppliers, deleteSupplier, clearErrors } from '../../actions/supplierActions';
import { DELETE_SUPPLIER_RESET } from '../../constants/supplierConstants';

const SupplierList = ({ history }) => {

    const alert = useAlert();
    const dispatch = useDispatch();

    // Lấy dữ liệu từ redux state
    const { loading, error, suppliers } = useSelector(state => state.suppliers);
    const { isDeleted } = useSelector(state => state.suppliers);

    useEffect(() => {
        if (isDeleted) {
            alert.success('Nhà cung cấp đã bị xóa thành công');
            dispatch(fetchSuppliers()); // Làm mới danh sách nhà cung cấp
            dispatch({ type: DELETE_SUPPLIER_RESET }); // Đặt lại trạng thái xóa
        }

        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }

        dispatch(fetchSuppliers()); // Lấy danh sách nhà cung cấp
    }, [dispatch, alert, error, isDeleted, history]);

    const deleteSupplierHandler = (id) => {
        dispatch(deleteSupplier(id));
    };

    const setSuppliers = () => {
        const data = {
            columns: [
                {
                    label: 'Tên nhà cung cấp',
                    field: 'name',
                    sort: 'asc',
                    width: 150
                },
                {
                    label: 'Sản phẩm cung cấp',
                    field: 'products',
                    sort: 'asc',
                    width: 250
                },
                {
                    label: 'Hành động',
                    field: 'actions',
                    sort: 'asc',
                    width: 100
                }
            ],
            rows: suppliers.map(supplier => ({
                name: supplier.name,
                products: supplier.products.join(', '), // Hiển thị danh sách sản phẩm
                actions: (
                    <Fragment>
                        <Link to={`/admin/supplier/${supplier._id}`} className="btn btn-primary py-1 px-2">
                            <i className="fa fa-pencil"></i>
                        </Link>
                        <button className="btn btn-danger py-1 px-2" onClick={() => deleteSupplierHandler(supplier._id)}>
                            <i className="fa fa-trash"></i>
                        </button>
                    </Fragment>
                )
            }))
        };

        return data;
    };

    return (
        <Fragment>
            <MetaData title={'Danh sách nhà cung cấp'} />
            <div className="row">
                <div className="col-12 col-md-2">
                    <Sidebar />
                </div>
                <div className="col-12 col-md-10">
                    <Fragment>
                        <h1 className="my-5">Danh sách nhà cung cấp</h1>
                        {loading ? <Loader /> : (
                            <MDBDataTable
                                data={setSuppliers()}
                                bordered
                                striped
                                hover
                            />
                        )}
                    </Fragment>
                </div>
            </div>
        </Fragment>
    );
};

export default SupplierList;
