import React, { useState, useEffect, Fragment } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useAlert } from 'react-alert';
import { useHistory, useParams } from 'react-router-dom';
import { fetchSuppliers, updateSupplier, clearErrors } from '../../actions/supplierActions';
import { UPDATE_SUPPLIER_RESET } from '../../constants/supplierConstants';
import Loader from '../layout/Loader';
import Sidebar from './Sidebar';

const EditSupplier = () => {
    const { id } = useParams(); // Lấy id nhà cung cấp từ URL
    const dispatch = useDispatch();
    const alert = useAlert();
    const history = useHistory();

    const { loading, error, supplier } = useSelector(state => state.supplierDetails);
    const { success } = useSelector(state => state.supplierUpdate);

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');

    useEffect(() => {
        if (success) {
            alert.success('Cập nhật nhà cung cấp thành công');
            history.push('/admin/suppliers');
            dispatch({ type: UPDATE_SUPPLIER_RESET });
        }

        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }

        // Lấy thông tin nhà cung cấp khi component mount
        if (!supplier || supplier._id !== id) {
            dispatch(fetchSuppliers(id));
        } else {
            setName(supplier.name);
            setEmail(supplier.email);
            setPhone(supplier.phone);
            setAddress(supplier.address);
        }
    }, [dispatch, alert, error, success, history, id, supplier]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const updatedSupplier = { name, email, phone, address };
        dispatch(updateSupplier(id, updatedSupplier));
    };

    return (
        <Fragment>
            <Sidebar />
            <div className="container">
                <h1 className="my-5">Chỉnh sửa nhà cung cấp</h1>
                {loading ? <Loader /> : (
                    <form onSubmit={handleSubmit}>
                        <div className="form-group">
                            <label htmlFor="name">Tên nhà cung cấp</label>
                            <input
                                type="text"
                                id="name"
                                className="form-control"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input
                                type="email"
                                id="email"
                                className="form-control"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="phone">Số điện thoại</label>
                            <input
                                type="text"
                                id="phone"
                                className="form-control"
                                value={phone}
                                onChange={(e) => setPhone(e.target.value)}
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="address">Địa chỉ</label>
                            <textarea
                                id="address"
                                className="form-control"
                                value={address}
                                onChange={(e) => setAddress(e.target.value)}
                            />
                        </div>

                        <button type="submit" className="btn btn-primary">Cập nhật nhà cung cấp</button>
                    </form>
                )}
            </div>
        </Fragment>
    );
};

export default EditSupplier;
