import React, { Fragment, useState, useEffect } from 'react'

import MetaData from '../layout/MetaData'
import Sidebar from './Sidebar'

import { useAlert } from 'react-alert'
import { useDispatch, useSelector } from 'react-redux'
import { createUser, clearErrors } from '../../actions/userActions' // Thay đổi action
import { CREATE_USER_RESET } from '../../constants/userConstants' // Thay đổi constant

const NewUser = ({ history }) => {

    const [avatar, setAvatar] = useState(null); // Lưu ảnh đại diện
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('');
    const [cccd, setCccd] = useState('');
    const [bankAccount, setBankAccount] = useState('');

    const roles = [
        'admin', 'staff', 'user' // Các vai trò người dùng
    ]

    const alert = useAlert();
    const dispatch = useDispatch();

    const newUserState = useSelector(state => state.newUser);
    const { error, success } = newUserState || {}; // Thay đổi state

    useEffect(() => {
        if (error) {
            alert.error(error);
            dispatch(clearErrors())
        }

        if (success) {
            history.push('/admin/users'); // Thay đổi đường dẫn
            alert.success('Tạo người dùng thành công');
            dispatch({ type: CREATE_USER_RESET }) // Thay đổi constant
        }

    }, [dispatch, alert, error, success, history])

    const submitHandler = (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.set('name', name);
        formData.set('email', email);
        formData.set('password', password);
        formData.set('role', role);
        formData.set('cccd', cccd);
        formData.set('bankAccount', bankAccount);

        // Thêm ảnh vào FormData
        if (avatar) {
            formData.set('avatar', avatar);
        }

        dispatch(createUser(formData)); // Gọi action tạo người dùng
    }

    return (
        <Fragment>
            <MetaData title={'Tạo người dùng mới'} />
            <div className="row">
                <div className="col-12 col-md-2">
                    <Sidebar />
                </div>

                <div className="col-12 col-md-10">
                    <Fragment>
                        <div className="wrapper my-5">
                            <form className="shadow-lg" onSubmit={submitHandler} encType='multipart/form-data'>
                                <h1 className="mb-4">Tạo người dùng mới</h1>

                                <div className="form-group">
                                    <label htmlFor="name_field">Tên người dùng</label>
                                    <input
                                        type="text"
                                        id="name_field"
                                        className="form-control"
                                        value={name}
                                        onChange={(e) => setName(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="email_field">Email</label>
                                    <input
                                        type="email"
                                        id="email_field"
                                        className="form-control"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="password_field">Mật khẩu</label>
                                    <input
                                        type="password"
                                        id="password_field"
                                        className="form-control"
                                        value={password}
                                        onChange={(e) => setPassword(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="role_field">Vai trò</label>
                                    <select className="form-control" id="role_field" value={role} onChange={(e) => setRole(e.target.value)}>
                                        {roles.map(role => (
                                            <option key={role} value={role}>{role}</option>
                                        ))}
                                    </select>
                                </div>

                                <div className="form-group">
                                    <label htmlFor="cccd_field">CCCD</label>
                                    <input
                                        type="text"
                                        id="cccd_field"
                                        className="form-control"
                                        value={cccd}
                                        onChange={(e) => setCccd(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="bankAccount_field">Số tài khoản ngân hàng</label>
                                    <input
                                        type="text"
                                        id="bankAccount_field"
                                        className="form-control"
                                        value={bankAccount}
                                        onChange={(e) => setBankAccount(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="avatar_field">Ảnh đại diện</label>
                                    <input
                                        type="file"
                                        id="avatar_field"
                                        className="form-control-file"
                                        onChange={(e) => setAvatar(e.target.files[0])}
                                    />
                                </div>

                                <button
                                    type="submit"
                                    className="btn btn-block py-3"
                                >
                                    Tạo mới
                                </button>

                            </form>
                        </div>
                    </Fragment>
                </div>
            </div>
        </Fragment>
    )
}

export default NewUser;
