import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSupplierDetail } from '../../actions/supplierActions'; // Đảm bảo action creator đã được import
import { useParams } from 'react-router-dom';

const EditSupplier = () => {
    const { id } = useParams(); // Lấy id nhà cung cấp từ URL
    const dispatch = useDispatch();

    // Lấy thông tin từ Redux store
    const { supplier, loading, error } = useSelector((state) => state.supplierDetails);

    // State của component
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');

    useEffect(() => {
        if (id) {
            dispatch(fetchSupplierDetail(id)); // Fetch dữ liệu nhà cung cấp
        }
    }, [dispatch, id]);

    // Kiểm tra trạng thái loading hoặc có lỗi
    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    // Nếu có dữ liệu nhà cung cấp
    useEffect(() => {
        if (supplier) {
            setName(supplier.name || '');
            setEmail(supplier.email || '');
            setPhone(supplier.phone || '');
            setAddress(supplier.address || '');
        }
    }, [supplier]);

    return (
        <div>
            <h1>Edit Supplier</h1>
            <form>
                <div>
                    <label>Name:</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </div>
                <div>
                    <label>Phone:</label>
                    <input
                        type="text"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                    />
                </div>
                <div>
                    <label>Address:</label>
                    <input
                        type="text"
                        value={address}
                        onChange={(e) => setAddress(e.target.value)}
                    />
                </div>
                <button type="submit">Update Supplier</button>
            </form>
        </div>
    );
};

export default EditSupplier;
