import React, { Fragment, useState, useEffect } from 'react';
import MetaData from '../layout/MetaData';
import Sidebar from './Sidebar';
import { useAlert } from 'react-alert';
import { useDispatch, useSelector } from 'react-redux';
import { newProduct, clearErrors } from '../../actions/productActions';
import { NEW_PRODUCT_RESET } from '../../constants/productConstants';

const NewProduct = ({ history }) => {
    const [name, setName] = useState('');
    const [price, setPrice] = useState(0);
    const [description, setDescription] = useState('');
    const [category, setCategory] = useState('');
    const [stock, setStock] = useState(0);
    const [supplierProduct, setSupplierProduct] = useState('');
    const [images, setImages] = useState([]);
    const [imagesPreview, setImagesPreview] = useState([]);
    const [suppliers, setSuppliers] = useState([]);

    const categories = [
        'Hải sản',
        'Truyền thống',
        'Thập cẩm',
        'Chay',
        '1 piece',
        'Khai vị',
        "Đồ uống",
        'Tráng miệng'
    ];

    const alert = useAlert();
    const dispatch = useDispatch();

    const { error, success } = useSelector(state => state.newProduct);

    useEffect(() => {
        // Fetch suppliers directly from API
        fetch('/api/suppliers')  // Đường dẫn API lấy danh sách nhà cung cấp
            .then(response => response.json())
            .then(data => setSuppliers(data))
            .catch(error => alert.error('Lỗi khi tải nhà cung cấp'));

        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }

        if (success) {
            history.push('/admin/products');
            alert.success('Thêm sản phẩm thành công');
            dispatch({ type: NEW_PRODUCT_RESET });
        }

    }, [dispatch, alert, error, success, history]);

    const submitHandler = (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.set('name', name);
        formData.set('price', price);
        formData.set('description', description);
        formData.set('category', category);
        formData.set('stock', stock);
        formData.set('supplierProduct', supplierProduct);

        images.forEach(image => {
            formData.append('images', image);
        });

        dispatch(newProduct(formData));
    };

    const onChange = e => {
        const files = Array.from(e.target.files);
        setImagesPreview([]);
        setImages([]);
        files.forEach(file => {
            const reader = new FileReader();
            reader.onload = () => {
                if (reader.readyState === 2) {
                    setImagesPreview(oldArray => [...oldArray, reader.result]);
                    setImages(oldArray => [...oldArray, reader.result]);
                }
            };
            reader.readAsDataURL(file);
        });
    };

    return (
        <Fragment>
            <MetaData title={'Thêm sản phẩm mới'} />
            <div className="row">
                <div className="col-12 col-md-2">
                    <Sidebar />
                </div>

                <div className="col-12 col-md-10">
                    <Fragment>
                        <div className="wrapper my-5">
                            <form className="shadow-lg" onSubmit={submitHandler} encType='multipart/form-data'>
                                <h1 className="mb-4">Thêm sản phẩm mới</h1>

                                {/* Các trường nhập liệu ở đây... */}

                                <div className="form-group">
                                    <label htmlFor="supplierProduct_field">Nhà cung cấp</label>
                                    <select
                                        id="supplierProduct_field"
                                        className="form-control"
                                        value={supplierProduct}
                                        onChange={(e) => setSupplierProduct(e.target.value)}
                                    >
                                        {suppliers.length > 0 ? (
                                            suppliers.map(supplier => (
                                                <option key={supplier._id} value={supplier._id}>{supplier.name}</option>
                                            ))
                                        ) : (
                                            <option>Không có nhà cung cấp nào</option>
                                        )}
                                    </select>
                                </div>

                                <div className='form-group'>
                                    <label>Hình sản phẩm</label>
                                    <div className='custom-file'>
                                        <input
                                            type='file'
                                            name='product_images'
                                            className='custom-file-input'
                                            id='customFile'
                                            onChange={onChange}
                                            multiple
                                        />
                                        <label className='custom-file-label' htmlFor='customFile'>
                                            Chọn hình
                                        </label>
                                    </div>

                                    {imagesPreview.map(img => (
                                        <img src={img} key={img} alt="Images Preview" className="mt-3 mr-2" width="55" height="52" />
                                    ))}
                                </div>
                                <button
                                    id="login_button"
                                    type="submit"
                                    className="btn btn-block py-3"
                                    disabled={suppliers.length === 0}
                                >
                                    Thêm sản phẩm
                                </button>
                            </form>
                        </div>
                    </Fragment>
                </div>
            </div>
        </Fragment>
    );
};

export default NewProduct;
