import {
    GET_SUPPLIERS_REQUEST,
    GET_SUPPLIERS_SUCCESS,
    GET_SUPPLIERS_FAIL,
    CREATE_SUPPLIER_SUCCESS,
    CREATE_SUPPLIER_FAIL,
    DELETE_SUPPLIER_SUCCESS,
    DELETE_SUPPLIER_FAIL,
    UPDATE_SUPPLIER_SUCCESS,
    UPDATE_SUPPLIER_FAIL,
    SUPPLIER_DETAILS_REQUEST,
    SUPPLIER_DETAILS_SUCCESS,
    SUPPLIER_DETAILS_FAIL,
    CLEAR_ERRORS
} from '../constants/supplierConstants';
import axios from 'axios';

// Lấy danh sách nhà cung cấp
export const fetchSuppliers = () => async (dispatch) => {
    try {
        dispatch({ type: GET_SUPPLIERS_REQUEST });

        // Gọi API trực tiếp để lấy danh sách nhà cung cấp
        const { data } = await axios.get('/api/v1/suppliers');
        
        dispatch({
            type: GET_SUPPLIERS_SUCCESS,
            payload: data.suppliers,
        });
    } catch (error) {
        const errorMessage = error.response && error.response.data.message 
            ? error.response.data.message 
            : error.message;
        dispatch({
            type: GET_SUPPLIERS_FAIL,
            payload: errorMessage,
        });
    }
};

// Thêm mới nhà cung cấp
export const addSupplier = (supplierData) => async (dispatch) => {
    try {
        dispatch({ type: CREATE_SUPPLIER_REQUEST });

        const config = { headers: { 'Content-Type': 'application/json' } };

        const { data } = await axios.post('/api/v1/suppliers', supplierData, config);

        dispatch({
            type: CREATE_SUPPLIER_SUCCESS,
            payload: data
        });
    } catch (error) {
        dispatch({
            type: CREATE_SUPPLIER_FAIL,
            payload: error.response.data.message
        });
    }
};

// Cập nhật nhà cung cấp
export const editSupplier = (id, supplierData) => async (dispatch) => {
    try {
        // Gọi API để cập nhật nhà cung cấp
        const { data } = await axios.put(`/api/v1/suppliers/${id}`, supplierData);

        dispatch({
            type: UPDATE_SUPPLIER_SUCCESS,
            payload: data.supplier,
        });
    } catch (error) {
        const errorMessage = error.response && error.response.data.message 
            ? error.response.data.message 
            : error.message;
        dispatch({
            type: UPDATE_SUPPLIER_FAIL,
            payload: errorMessage,
        });
    }
};

// Xóa nhà cung cấp
export const removeSupplier = (id) => async (dispatch) => {
    try {
        // Gọi API để xóa nhà cung cấp
        await axios.delete(`/api/v1/suppliers/${id}`);

        dispatch({
            type: DELETE_SUPPLIER_SUCCESS,
            payload: id,
        });
    } catch (error) {
        const errorMessage = error.response && error.response.data.message 
            ? error.response.data.message 
            : error.message;
        dispatch({
            type: DELETE_SUPPLIER_FAIL,
            payload: errorMessage,
        });
    }
};

export const fetchSupplierDetails = (id) => async (dispatch) => {
    try {
        dispatch({ type: SUPPLIER_DETAILS_REQUEST });

        const { data } = await axios.get(`/api/suppliers/${id}`);

        dispatch({
            type: SUPPLIER_DETAILS_SUCCESS,
            payload: data,
        });
    } catch (error) {
        dispatch({
            type: SUPPLIER_DETAILS_FAIL,
            payload: error.response.data.message,
        });
    }
};


export const clearErrors = () => (dispatch) => {
    dispatch({
        type: CLEAR_ERRORS
    });
};
