import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createUser, clearErrors } from '../../actions/userActions'; 
import { useAlert } from 'react-alert';

const NewUser = ({ history }) => {
    const alert = useAlert();
    const dispatch = useDispatch();

    const { error, success } = useSelector(state => state.userCreate);

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [role, setRole] = useState('staff'); 
    const [cccd, setCccd] = useState('');
    const [bankAccount, setBankAccount] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [formErrors, setFormErrors] = useState({});

    useEffect(() => {
        if (success) {
            alert.success('Nhân viên đã được tạo');
            // Không cần điều hướng nữa
        }

        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }
    }, [dispatch, alert, error, success]);

    const validateForm = () => {
        const errors = {};
        if (!name) errors.name = 'Tên không được để trống';
        if (!email || !/\S+@\S+\.\S+/.test(email)) errors.email = 'Email không hợp lệ';
        if (!password) errors.password = 'Mật khẩu không được để trống';
        if (password !== confirmPassword) errors.confirmPassword = 'Mật khẩu xác nhận không khớp';
        if (role === 'staff') {
            if (!cccd) errors.cccd = 'Số CCCD không được để trống';
            if (!bankAccount) errors.bankAccount = 'Tài khoản ngân hàng không được để trống';
        }
        setFormErrors(errors);
        return Object.keys(errors).length === 0;
    };

    const submitHandler = (e) => {
        e.preventDefault();

        if (validateForm()) {
            dispatch(createUser({ name, email, password, role, cccd, bankAccount }));
        }
    };

    return (
        <form onSubmit={submitHandler}>
            <div className="form-group">
                <label htmlFor="name">Tên</label>
                <input
                    type="text"
                    id="name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                    className="form-control"
                />
                {formErrors.name && <small className="text-danger">{formErrors.name}</small>}
            </div>
            <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    className="form-control"
                />
                {formErrors.email && <small className="text-danger">{formErrors.email}</small>}
            </div>
            <div className="form-group">
                <label htmlFor="role">Vai trò</label>
                <select
                    id="role"
                    value={role}
                    onChange={(e) => setRole(e.target.value)}
                    className="form-control"
                >
                    <option value="staff">Nhân viên</option>
                    <option value="user">Người dùng</option>
                    <option value="admin">Quản trị viên</option>
                </select>
            </div>
            {role === 'staff' && (
                <>
                    <div className="form-group">
                        <label htmlFor="cccd">Số CCCD</label>
                        <input
                            type="text"
                            id="cccd"
                            value={cccd}
                            onChange={(e) => setCccd(e.target.value)}
                            className="form-control"
                        />
                        {formErrors.cccd && <small className="text-danger">{formErrors.cccd}</small>}
                    </div>
                    <div className="form-group">
                        <label htmlFor="bankAccount">Tài khoản ngân hàng</label>
                        <input
                            type="text"
                            id="bankAccount"
                            value={bankAccount}
                            onChange={(e) => setBankAccount(e.target.value)}
                            className="form-control"
                        />
                        {formErrors.bankAccount && <small className="text-danger">{formErrors.bankAccount}</small>}
                    </div>
                </>
            )}
            <div className="form-group">
                <label htmlFor="password">Mật khẩu</label>
                <input
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    className="form-control"
                />
                {formErrors.password && <small className="text-danger">{formErrors.password}</small>}
            </div>
            <div className="form-group">
                <label htmlFor="confirmPassword">Xác nhận mật khẩu</label>
                <input
                    type="password"
                    id="confirmPassword"
                    value={confirmPassword}
                    onChange={(e) => setConfirmPassword(e.target.value)}
                    className="form-control"
                />
                {formErrors.confirmPassword && <small className="text-danger">{formErrors.confirmPassword}</small>}
            </div>
            <button type="submit" className="btn btn-primary">Tạo nhân viên</button>
        </form>
    );
};

export default NewUser;
