import React, { Fragment, useState, useEffect } from 'react';
import Modal from 'react-modal'; // Import Modal
import Pagination from 'react-js-pagination';
import Slider from 'rc-slider';
import 'rc-slider/assets/index.css';

import MetaData from './layout/MetaData';
import Product from './product/Product';
import Loader from './layout/Loader';

import { useDispatch, useSelector } from 'react-redux';
import { useAlert } from 'react-alert';
import { getProducts } from '../actions/productActions';

import Sliderr from '../components/layout/Slider';

const { createSliderWithTooltip } = Slider;
const Range = createSliderWithTooltip(Slider.Range);

Modal.setAppElement('#root'); // Khai báo cho react-modal

const Home = ({ match }) => {
    const [currentPage, setCurrentPage] = useState(1);
    const [price, setPrice] = useState([1, 1000000]);
    const [category, setCategory] = useState('');
    const [rating, setRating] = useState(0);
    const [isModalOpen, setIsModalOpen] = useState(true); // Trạng thái của modal

    const categories = [
        'Hải sản',
        'Truyền thống',
        'Thập cẩm',
        'Chay',
        '1 piece',
        'Khai vị',
        'Đồ uống',
        'Tráng miệng',
    ];

    const alert = useAlert();
    const dispatch = useDispatch();

    const { loading, products, error, productsCount, resPerPage, filteredProductsCount } = useSelector(
        (state) => state.products
    );

    const keyword = match.params.keyword;

    useEffect(() => {
        if (error) {
            return alert.error(error);
        }

        dispatch(getProducts(keyword, currentPage, price, category, rating));
    }, [dispatch, alert, error, keyword, currentPage, price, category, rating]);

    function setCurrentPageNo(pageNumber) {
        setCurrentPage(pageNumber);
    }

    let count = productsCount;
    if (keyword) {
        count = filteredProductsCount;
    }

    return (
        <Fragment>
            {/* Modal hiển thị thông báo */}
            <Modal
                isOpen={isModalOpen}
                onRequestClose={() => setIsModalOpen(false)}
                style={{
                    content: {
                        width: '40%',
                        height: '30%',
                        margin: 'auto',
                        borderRadius: '10px',
                        textAlign: 'center',
                        padding: '20px',
                    },
                }}
            >
                <h2>Thông báo</h2>
                <p>Shop chỉ giao đồ ăn trong khu vực thành phố Hồ Chí Minh.</p>
                <button
                    onClick={() => setIsModalOpen(false)}
                    style={{
                        padding: '10px 20px',
                        background: '#28a745',
                        color: '#fff',
                        border: 'none',
                        borderRadius: '5px',
                        cursor: 'pointer',
                    }}
                >
                    OK
                </button>
            </Modal>

            <Sliderr />
            <br />
            <h3 className="text-center"></h3>
            <div id="products_heading" className="container container-fluid">
                <ul id="ul_top_hypers">
                    {categories.map((category) => (
                        <li
                            style={{
                                cursor: 'pointer',
                                listStyleType: 'none',
                            }}
                            key={category}
                            onClick={() => setCategory(category)}
                        >
                            <div className="button">
                                <p className="btnText">{category}</p>
                                <div className="btnTwo">
                                    <p className="btnText2">Chọn!</p>
                                </div>
                            </div>
                        </li>
                    ))}
                </ul>
            </div>
            <br />
            {loading ? (
                <Loader />
            ) : (
                <Fragment>
                    <MetaData title={'Pizza KPT'} />
                    <section id="products" className="container mt-5">
                        <div className="row">
                            {keyword
                                ? products.map((product) => (
                                      <Product key={product._id} product={product} col={4} />
                                  ))
                                : products.map((product) => (
                                      <Product key={product._id} product={product} col={3} />
                                  ))}
                        </div>
                    </section>

                    {resPerPage <= count && (
                        <div className="d-flex justify-content-center mt-5">
                            <Pagination
                                activePage={currentPage}
                                itemsCountPerPage={resPerPage}
                                totalItemsCount={productsCount}
                                onChange={setCurrentPageNo}
                                nextPageText={'Tiếp'}
                                prevPageText={'Trở về'}
                                firstPageText={'Đầu tiên'}
                                lastPageText={'Cuối cùng'}
                                itemClass="page-item"
                                linkClass="page-link"
                            />
                        </div>
                    )}
                </Fragment>
            )}
        </Fragment>
    );
};

export default Home;
