import React, { useState, useEffect, Fragment } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updateSupplier, fetchSupplierDetail, clearErrors } from '../../actions/supplierActions';
import { UPDATE_SUPPLIER_RESET } from '../../constants/supplierConstants';
import { useAlert } from 'react-alert';
import { useHistory, useParams } from 'react-router-dom';
import Sidebar from './Sidebar'; // Import Sidebar
import axios from 'axios'; // Dùng để lấy danh sách sản phẩm

const EditSupplier = () => {
    const { id } = useParams(); // Lấy id nhà cung cấp từ URL
    const dispatch = useDispatch();
    const alert = useAlert();
    const history = useHistory();

    // Lấy thông tin từ Redux state
    const { supplier = {}, loading, error } = useSelector((state) => state.supplierDetails);
    const { success: updateSuccess, error: updateError } = useSelector((state) => state.updateSupplier);

    // State để quản lý form
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');
    const [productsSupplied, setProductsSupplied] = useState([]); // Mảng ID sản phẩm cung cấp

    // Khi cập nhật thành công, reset form hoặc chuyển hướng
    useEffect(() => {
        if (updateError) {
            alert.error(updateError);
            dispatch(clearErrors()); // Xóa lỗi
        }

        if (updateSuccess) {
            alert.success('Cập nhật nhà cung cấp thành công!');
            dispatch({ type: UPDATE_SUPPLIER_RESET });
            history.push('/admin/suppliers'); // Chuyển hướng sau khi cập nhật thành công
        }

        if (!supplier.name || supplier._id !== id) {
            dispatch(fetchSupplierDetail(id)); // Lấy thông tin nhà cung cấp từ server nếu chưa có
        } else {
            setName(supplier.name);
            setEmail(supplier.email);
            setPhone(supplier.phone);
            setAddress(supplier.address);
            setProductsSupplied(supplier.productsSupplied); // Cập nhật các sản phẩm đã cung cấp
        }
    }, [dispatch, alert, id, supplier, updateError, updateSuccess, history]);

    // Hàm xử lý thay đổi trong form
    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'name') setName(value);
        if (name === 'email') setEmail(value);
        if (name === 'phone') setPhone(value);
        if (name === 'address') setAddress(value);
        if (name === 'productsSupplied') setProductsSupplied(value); // Cập nhật sản phẩm cung cấp
    };

    // Hàm xử lý submit form
    const handleSubmit = (e) => {
        e.preventDefault();
        const updatedSupplier = {
            name,
            email,
            phone,
            address,
            productsSupplied, // Thêm sản phẩm cung cấp
        };
        dispatch(updateSupplier(id, updatedSupplier)); // Gửi action cập nhật
    };

    return (
        <Fragment>
            <Sidebar />
            <div className="container">
                <h1>Chỉnh sửa nhà cung cấp</h1>
                {loading ? (
                    <p>Loading...</p>
                ) : (
                    <form onSubmit={handleSubmit}>
                        <div>
                            <label htmlFor="name">Tên nhà cung cấp</label>
                            <input
                                type="text"
                                id="name"
                                name="name"
                                value={name}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label htmlFor="email">Email</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={email}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label htmlFor="phone">Số điện thoại</label>
                            <input
                                type="text"
                                id="phone"
                                name="phone"
                                value={phone}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label htmlFor="address">Địa chỉ</label>
                            <input
                                type="text"
                                id="address"
                                name="address"
                                value={address}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <div>
                            <label htmlFor="productsSupplied">Sản phẩm cung cấp</label>
                            <input
                                type="text"
                                id="productsSupplied"
                                name="productsSupplied"
                                value={productsSupplied}
                                onChange={handleChange}
                                required
                            />
                        </div>
                        <button type="submit">Cập nhật</button>
                    </form>
                )}
            </div>
        </Fragment>
    );
};

export default EditSupplier;
