import React, { Fragment, useState } from 'react'
// import { countries } from 'countries-list'

import MetaData from '../layout/MetaData'
import CheckoutSteps from './CheckoutSteps'

import { useDispatch, useSelector } from 'react-redux'
import { saveShippingInfo } from '../../actions/cartActions'

const Shipping = ({ history }) => {

    // const countriesList = Object.values(countries)

    const { shippingInfo } = useSelector(state => state.cart)

    const [address, setAddress] = useState(shippingInfo.address)
    const [city, setCity] = useState(shippingInfo.city)
    const [isPhoneInvalid, setIsPhoneInvalid] = useState(false);
    const [postalCode, setPostalCode] = useState(shippingInfo.postalCode)
    const [phoneNo, setPhoneNo] = useState(shippingInfo.phoneNo)
    const [country, setCountry] = useState('Vietnam')

    const dispatch = useDispatch();

    const submitHandler = (e) => {
        e.preventDefault()

        dispatch(saveShippingInfo({ address, city, phoneNo }))
        history.push('/confirm')
    }

    return (
        <Fragment>

            <MetaData title={'Thông tin vận chuyển'} />

            <CheckoutSteps shipping />

            <div className="row wrapper">
                <div className="col-10 col-lg-5">
                    <form className="shadow-lg" onSubmit={submitHandler}>
                        <h1 className="mb-4">Thông tin vận chuyển</h1>
                        <div className="form-group">
                            <label htmlFor="address_field">Địa chỉ</label>
                            <input
                                type="text"
                                id="address_field"
                                className="form-control"
                                value={address}
                                onChange={(e) => setAddress(e.target.value)}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="city_field">Thành phố</label>
                            <input
                                type="text"
                                id="city_field"
                                className="form-control"
                                value={city}
                                onChange={(e) => setCity(e.target.value)}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="phone_field">Số điện thoại</label>
                            <input
                                type="text"
                                id="phone_field"
                                className={`form-control ${isPhoneInvalid ? 'is-invalid' : ''}`} // Thêm class 'is-invalid' khi lỗi
                                value={phoneNo}
                                onChange={(e) => {
                                    const value = e.target.value;
                                    if (/^\d{0,10}$/.test(value)) {
                                        setPhoneNo(value);
                                        setIsPhoneInvalid(value.length !== 10); // Cập nhật trạng thái lỗi
                                    } else {
                                        setIsPhoneInvalid(true);
                                    }
                                }}
                                required
                            />
                            {isPhoneInvalid && (
                                <div className="invalid-feedback">Số điện thoại phải đúng 10 chữ số.</div>
                            )}
                        </div>

                        {/* <div className="form-group">
                            <label htmlFor="postal_code_field">Mã bưu điện</label>
                            <input
                                type="number"
                                id="postal_code_field"
                                className="form-control"
                                value={postalCode}
                                onChange={(e) => setPostalCode(e.target.value)}
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="country_field">Quốc gia</label>
                            <input
                                type="text"
                                id="country_field"
                                className="form-control"
                                value={country}
                                onChange={(e) => setCountry(e.target.value)}
                                required
                                disabled 
                            />
                        </div> */}

                        <button
                            id="shipping_btn"
                            type="submit"
                            className="btn btn-block py-3"
                            disabled={isPhoneInvalid} // Vô hiệu hóa khi số điện thoại không hợp lệ
                        >
                            TIẾP TỤC
                        </button>
                    </form>
                </div>
            </div>

        </Fragment>
    )
}

export default Shipping
