import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useAlert } from 'react-alert';
import { getSupplierDetails, updateSupplier, clearErrors } from '../../actions/supplierActions';
import { UPDATE_SUPPLIER_RESET } from '../../constants/supplierConstants';

const EditSupplier = ({ match, history }) => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');

    const alert = useAlert();
    const dispatch = useDispatch();

    const { error, supplier } = useSelector(state => state.supplierDetails);
    const { isUpdated } = useSelector(state => state.supplier);

    useEffect(() => {
        if (supplier && supplier._id !== match.params.id) {
            dispatch(getSupplierDetails(match.params.id));
        } else {
            setName(supplier.name || '');
            setEmail(supplier.email || '');
            setPhone(supplier.phone || '');
            setAddress(supplier.address || '');
        }

        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }

        if (isUpdated) {
            alert.success('Nhà cung cấp đã được cập nhật thành công');
            history.push('/admin/suppliers');
            dispatch({ type: UPDATE_SUPPLIER_RESET });
        }
    }, [dispatch, alert, error, isUpdated, supplier, match.params.id, history]);

    const submitHandler = (e) => {
        e.preventDefault();

        const formData = { name, email, phone, address };
        dispatch(updateSupplier(match.params.id, formData));
    };

    return (
        <div className="container">
            <h1 className="my-5">Cập nhật nhà cung cấp</h1>
            <form onSubmit={submitHandler}>
                <div className="form-group">
                    <label htmlFor="name">Tên nhà cung cấp</label>
                    <input
                        type="text"
                        id="name"
                        className="form-control"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="email">Email</label>
                    <input
                        type="email"
                        id="email"
                        className="form-control"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="phone">Điện thoại</label>
                    <input
                        type="text"
                        id="phone"
                        className="form-control"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="address">Địa chỉ</label>
                    <textarea
                        id="address"
                        className="form-control"
                        rows="3"
                        value={address}
                        onChange={(e) => setAddress(e.target.value)}
                        required
                    ></textarea>
                </div>
                <button type="submit" className="btn btn-primary">
                    Cập nhật
                </button>
            </form>
        </div>
    );
};

export default EditSupplier;
