import React, { Fragment, useState, useEffect } from 'react';

import MetaData from '../layout/MetaData';
import Sidebar from './Sidebar';

import { useAlert } from 'react-alert';
import { useDispatch, useSelector } from 'react-redux';
import { updateSupplier, getSupplierDetails, clearErrors } from '../../actions/supplierActions';
import { UPDATE_SUPPLIER_RESET } from '../../constants/supplierConstants';

const UpdateSupplier = ({ match, history }) => {

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [contact, setContact] = useState('');
    const [address, setAddress] = useState('');
    const [productSupplied, setProductSupplied] = useState('');
    const [status, setStatus] = useState('');

    const alert = useAlert();
    const dispatch = useDispatch();

    const { error, supplier } = useSelector(state => state.supplierDetails);
    const { loading, error: updateError, isUpdated } = useSelector(state => state.supplier);

    const supplierId = match.params.id;

    useEffect(() => {

        if (supplier && supplier._id !== supplierId) {
            dispatch(getSupplierDetails(supplierId));
        } else {
            setName(supplier.name);
            setEmail(supplier.email);
            setContact(supplier.contact);
            setAddress(supplier.address);
            setProductSupplied(supplier.productSupplied);
            setStatus(supplier.status);
        }

        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }

        if (updateError) {
            alert.error(updateError);
            dispatch(clearErrors());
        }

        if (isUpdated) {
            history.push('/admin/suppliers');
            alert.success('Supplier updated successfully');
            dispatch({ type: UPDATE_SUPPLIER_RESET });
        }

    }, [dispatch, alert, error, isUpdated, history, updateError, supplier, supplierId]);

    const submitHandler = (e) => {
        e.preventDefault();

        const supplierData = {
            name,
            email,
            contact,
            address,
            productSupplied,
            status
        };

        dispatch(updateSupplier(supplier._id, supplierData));
    };

    return (
        <Fragment>
            <MetaData title={'Update Supplier'} />
            <div className="row">
                <div className="col-12 col-md-2">
                    <Sidebar />
                </div>

                <div className="col-12 col-md-10">
                    <Fragment>
                        <div className="wrapper my-5">
                            <form className="shadow-lg" onSubmit={submitHandler}>
                                <h1 className="mb-4">Cập nhật nhà cung cấp</h1>

                                <div className="form-group">
                                    <label htmlFor="name_field">Tên nhà cung cấp</label>
                                    <input
                                        type="text"
                                        id="name_field"
                                        className="form-control"
                                        value={name}
                                        onChange={(e) => setName(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="email_field">Email</label>
                                    <input
                                        type="email"
                                        id="email_field"
                                        className="form-control"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="contact_field">Số điện thoại</label>
                                    <input
                                        type="text"
                                        id="contact_field"
                                        className="form-control"
                                        value={contact}
                                        onChange={(e) => setContact(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="address_field">Địa chỉ</label>
                                    <input
                                        type="text"
                                        id="address_field"
                                        className="form-control"
                                        value={address}
                                        onChange={(e) => setAddress(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="productSupplied_field">Sản phẩm cung cấp</label>
                                    <input
                                        type="text"
                                        id="productSupplied_field"
                                        className="form-control"
                                        value={productSupplied}
                                        onChange={(e) => setProductSupplied(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="status_field">Trạng thái</label>
                                    <select
                                        className="form-control"
                                        id="status_field"
                                        value={status}
                                        onChange={(e) => setStatus(e.target.value)}
                                    >
                                        <option value="Active">Hoạt động</option>
                                        <option value="Inactive">Không hoạt động</option>
                                    </select>
                                </div>

                                <button
                                    id="update_button"
                                    type="submit"
                                    className="btn btn-block py-3"
                                    disabled={loading ? true : false}
                                >
                                    Cập nhật
                                </button>
                            </form>
                        </div>
                    </Fragment>
                </div>
            </div>
        </Fragment>
    );
};

export default UpdateSupplier;
