import React, { useState, useEffect } from 'react';
import { useAlert } from 'react-alert';
import { useDispatch, useSelector } from 'react-redux';
import { updateSupplier, fetchSupplierDetail, clearErrors } from '../../actions/supplierActions';
import { UPDATE_SUPPLIER_RESET } from '../../constants/supplierConstants';
import { useHistory, useParams } from 'react-router-dom';
import Sidebar from './Sidebar';

const EditSupplier = () => {
    const { id } = useParams(); // Lấy id nhà cung cấp từ URL
    const dispatch = useDispatch();
    const alert = useAlert();
    const history = useHistory();

    // State để lưu trữ thông tin nhà cung cấp và quản lý form
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');
    const [productsSupplied, setProductsSupplied] = useState([]); // Mảng sản phẩm cung cấp

    // Redux State
    const { supplier, loading, error } = useSelector((state) => state.supplierDetails);
    const { success: updateSuccess, error: updateError } = useSelector((state) => state.updateSupplier);

    // Fetch supplier details khi component mount
    useEffect(() => {
        if (updateError) {
            alert.error(updateError);
            dispatch(clearErrors());
        }

        if (updateSuccess) {
            alert.success('Cập nhật nhà cung cấp thành công!');
            dispatch({ type: UPDATE_SUPPLIER_RESET }); // Reset state
            history.push('/admin/suppliers');
        }

        if (!supplier || supplier._id !== id) {
            dispatch(fetchSupplierDetail(id));
        } else {
            setName(supplier.name);
            setEmail(supplier.email);
            setPhone(supplier.phone);
            setAddress(supplier.address);
            setProductsSupplied(supplier.productsSupplied || []);
        }
    }, [dispatch, alert, id, supplier, updateError, updateSuccess, history]);

    const submitHandler = (e) => {
        e.preventDefault();

        const supplierData = {
            name,
            email,
            phone,
            address,
            productsSupplied,
        };

        dispatch(updateSupplier(id, supplierData));
    };

    return (
        <Fragment>
            <MetaData title={`Chỉnh sửa nhà cung cấp: ${name}`} />
            <div className="dashboard">
                <Sidebar />
                <div className="newProductContainer">
                    <form className="createProductForm" onSubmit={submitHandler}>
                        <h1>Chỉnh sửa nhà cung cấp</h1>

                        {loading ? <div>Đang tải...</div> : (
                            <Fragment>
                                <div>
                                    <label>Tên nhà cung cấp</label>
                                    <input
                                        type="text"
                                        value={name}
                                        onChange={(e) => setName(e.target.value)}
                                        required
                                    />
                                </div>

                                <div>
                                    <label>Email</label>
                                    <input
                                        type="email"
                                        value={email}
                                        onChange={(e) => setEmail(e.target.value)}
                                        required
                                    />
                                </div>

                                <div>
                                    <label>Số điện thoại</label>
                                    <input
                                        type="text"
                                        value={phone}
                                        onChange={(e) => setPhone(e.target.value)}
                                        required
                                    />
                                </div>

                                <div>
                                    <label>Địa chỉ</label>
                                    <input
                                        type="text"
                                        value={address}
                                        onChange={(e) => setAddress(e.target.value)}
                                        required
                                    />
                                </div>

                                <div>
                                    <label>Sản phẩm cung cấp</label>
                                    <input
                                        type="text"
                                        value={productsSupplied.join(', ')} // Hiển thị danh sách sản phẩm dưới dạng chuỗi
                                        onChange={(e) => setProductsSupplied(e.target.value.split(','))}
                                    />
                                </div>

                                <button type="submit" disabled={loading}>
                                    Cập nhật nhà cung cấp
                                </button>
                            </Fragment>
                        )}
                    </form>
                </div>
            </div>
        </Fragment>
    );
};

export default EditSupplier;
