import React, { useState, useEffect, Fragment } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updateSupplier, fetchSupplierDetail, clearErrors } from '../../actions/supplierActions'; 
import { UPDATE_SUPPLIER_RESET } from '../../constants/supplierConstants';
import { useAlert } from 'react-alert';
import { useHistory, useParams } from 'react-router-dom';
import Sidebar from './Sidebar'; 
import axios from 'axios';

const EditSupplier = () => {
    const { id } = useParams(); // Lấy id nhà cung cấp từ URL
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');
    const [productsSupplied, setProductsSupplied] = useState([]);
    const [products, setProducts] = useState([]);

    const dispatch = useDispatch();
    const alert = useAlert();
    const history = useHistory();

    const { error, success, supplier } = useSelector(state => state.supplierDetails || {});
    const { loading, error: updateError } = useSelector(state => state.supplierUpdate || {}); // Lỗi khi cập nhật nhà cung cấp

    useEffect(() => {
        if (id) {
            dispatch(fetchSupplierDetail(id)); // Gọi action lấy thông tin chi tiết nhà cung cấp

            if (error) {
                alert.error(error);
                dispatch(clearErrors());
            }

            if (success) {
                alert.success('Nhà cung cấp đã được cập nhật');
                history.push('/admin/suppliers');
                dispatch({ type: UPDATE_SUPPLIER_RESET });
            }
        }

        // Lấy danh sách sản phẩm
        const fetchProducts = async () => {
            try {
                const response = await axios.get('/api/v1/products');
                setProducts(response.data.products);
            } catch (err) {
                alert.error('Không thể lấy danh sách sản phẩm');
            }
        };

        fetchProducts();
    }, [dispatch, alert, error, success, history, id]);

    useEffect(() => {
        if (loading) return;
        if (supplier) {
            console.log('Supplier details from Redux: ', supplier);
            setName(supplier.name);
            setEmail(supplier.email);
            setPhone(supplier.phone);
            setAddress(supplier.address);
            setProductsSupplied(supplier.productsSupplied.map(product => product._id)); 
        }
    }, [supplier, loading]);

    const handleSubmit = (e) => {
        e.preventDefault();

        const supplierData = {
            name,
            email,
            phone,
            address,
            productsSupplied
        };

        dispatch(updateSupplier(id, supplierData));
    };

    return (
        <Fragment>
            <div className="row">
                <div className="col-12 col-md-2">
                    <Sidebar />
                </div>

                <div className="col-12 col-md-10">
                    <div className="container">
                        {loading || !supplier || Object.keys(supplier).length === 0 ? (
                            <div>Loading supplier data...</div>
                        ) : (
                            <>
                                <h1 className="my-5">Cập nhật nhà cung cấp</h1>

                                <form onSubmit={handleSubmit}>
                                    <div className="form-group">
                                        <label htmlFor="name">Tên nhà cung cấp</label>
                                        <input 
                                            type="text" 
                                            id="name" 
                                            className="form-control" 
                                            value={name}
                                            onChange={(e) => setName(e.target.value)} 
                                            required 
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="email">Email</label>
                                        <input 
                                            type="email" 
                                            id="email" 
                                            className="form-control" 
                                            value={email}
                                            onChange={(e) => setEmail(e.target.value)} 
                                            required 
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="phone">Số điện thoại</label>
                                        <input 
                                            type="text" 
                                            id="phone" 
                                            className="form-control" 
                                            value={phone}
                                            onChange={(e) => setPhone(e.target.value)} 
                                            required 
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="address">Địa chỉ</label>
                                        <textarea 
                                            id="address" 
                                            className="form-control" 
                                            value={address}
                                            onChange={(e) => setAddress(e.target.value)} 
                                            required 
                                        />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="productsSupplied">Sản phẩm cung cấp</label>
                                        <select
                                            id="productsSupplied"
                                            className="form-control"
                                            multiple
                                            value={productsSupplied}
                                            onChange={(e) => setProductsSupplied(Array.from(e.target.selectedOptions, option => option.value))}
                                            required
                                        >
                                            {products.map(product => (
                                                <option key={product._id} value={product._id}>
                                                    {product.name}
                                                </option>
                                            ))}
                                        </select>
                                    </div>

                                    <button 
                                        type="submit" 
                                        className="btn btn-primary mt-3" 
                                        disabled={loading}
                                    >
                                        {loading ? 'Đang cập nhật...' : 'Cập nhật nhà cung cấp'}
                                    </button>
                                </form>
                            </>
                        )}
                    </div>
                </div>
            </div>
        </Fragment>
    );    
};

export default EditSupplier;
