import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { editSupplier, fetchSupplierDetails, clearErrors } from '../../actions/supplierActions';
import { useAlert } from 'react-alert';
import { UPDATE_SUPPLIER_RESET } from '../../constants/supplierConstants';
import Loader from '../layout/Loader';

const UpdateSupplier = ({ match, history }) => {
    const alert = useAlert();
    const dispatch = useDispatch();

    const { id } = match.params;

    const { error, supplier } = useSelector(state => state.supplierDetails || {});
    const { loading, isUpdated } = useSelector(state => state.supplier || {});

    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');

    useEffect(() => {
        if (!supplier) {
            return <div>Loading...</div>;
        }
        
        setName(supplier.name);
        setEmail(supplier.email);
        setPhone(supplier.phone);
        setAddress(supplier.address);

        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }

        if (isUpdated) {
            alert.success('Nhà cung cấp đã được cập nhật');
            history.push('/admin/suppliers');
            dispatch({ type: UPDATE_SUPPLIER_RESET });
        }

    }, [dispatch, alert, error, isUpdated, id, supplier, history]);

    const submitHandler = (e) => {
        e.preventDefault();
        const updatedSupplier = { name, email, phone, address };
        dispatch(editSupplier(id, updatedSupplier));
    };

    return (
        <div>
            <h1>Cập nhật nhà cung cấp</h1>
            {loading ? <Loader /> : (
                <form onSubmit={submitHandler}>
                    <div className="form-group">
                        <label htmlFor="name">Tên nhà cung cấp</label>
                        <input
                            type="text"
                            id="name"
                            className="form-control"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="email"
                            id="email"
                            className="form-control"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="phone">Điện thoại</label>
                        <input
                            type="text"
                            id="phone"
                            className="form-control"
                            value={phone}
                            onChange={(e) => setPhone(e.target.value)}
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="address">Địa chỉ</label>
                        <textarea 
                            id="address" 
                            className="form-control" 
                            value={address}
                            onChange={(e) => setAddress(e.target.value)} 
                            required 
                        />
                    </div>

                    <button type="submit" className="btn btn-primary">Cập nhật</button>
                </form>
            )}
        </div>
    );
};

export default UpdateSupplier;
