import {
    GET_SUPPLIERS_REQUEST,
    GET_SUPPLIERS_SUCCESS,
    GET_SUPPLIERS_FAIL,
    CREATE_SUPPLIER_SUCCESS,
    CREATE_SUPPLIER_FAIL,
    DELETE_SUPPLIER_SUCCESS,
    DELETE_SUPPLIER_FAIL,
    UPDATE_SUPPLIER_SUCCESS,
    UPDATE_SUPPLIER_FAIL,
} from '../constants/supplierConstants';
import {
    getSuppliers,
    createSupplier,
    deleteSupplier,
    updateSupplier,
} from '../services/supplierService';

// Lấy danh sách nhà cung cấp
export const fetchSuppliers = () => async (dispatch) => {
    try {
        dispatch({ type: GET_SUPPLIERS_REQUEST });
        const suppliers = await getSuppliers();
        dispatch({ type: GET_SUPPLIERS_SUCCESS, payload: suppliers });
    } catch (error) {
        const errorMessage = error.response && error.response.data.message ? error.response.data.message : error.message;
        dispatch({ type: GET_SUPPLIERS_FAIL, payload: errorMessage });
    }
};

// Thêm mới nhà cung cấp
export const addSupplier = (supplierData) => async (dispatch) => {
    try {
        const newSupplier = await createSupplier(supplierData);
        dispatch({ type: CREATE_SUPPLIER_SUCCESS, payload: newSupplier });
    } catch (error) {
        const errorMessage = error.response && error.response.data.message ? error.response.data.message : error.message;
        dispatch({ type: CREATE_SUPPLIER_FAIL, payload: errorMessage });
    }
};

// Cập nhật nhà cung cấp
export const editSupplier = (id, supplierData) => async (dispatch) => {
    try {
        const updatedSupplier = await updateSupplier(id, supplierData);
        dispatch({ type: UPDATE_SUPPLIER_SUCCESS, payload: updatedSupplier });
    } catch (error) {
        const errorMessage = error.response && error.response.data.message ? error.response.data.message : error.message;
        dispatch({ type: UPDATE_SUPPLIER_FAIL, payload: errorMessage });
    }
};

// Xóa nhà cung cấp
export const removeSupplier = (id) => async (dispatch) => {
    try {
        await deleteSupplier(id);
        dispatch({ type: DELETE_SUPPLIER_SUCCESS, payload: id });
    } catch (error) {
        const errorMessage = error.response && error.response.data.message ? error.response.data.message : error.message;
        dispatch({ type: DELETE_SUPPLIER_FAIL, payload: errorMessage });
    }
};
