import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useHistory } from 'react-router-dom';

const UpdateSupplier = () => {
    const { id } = useParams();  // Lấy id nhà cung cấp từ URL
    const history = useHistory(); // Để chuyển hướng người dùng sau khi cập nhật thành công
    const [supplier, setSupplier] = useState({
        name: '',
        email: '',
        phone: '',
        address: '',
        productsSupplied: []
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        // Tải thông tin nhà cung cấp khi component được render
        const fetchSupplier = async () => {
            try {
                const response = await axios.get(`/api/suppliers/${id}`);
                setSupplier(response.data.supplier);
            } catch (err) {
                setError('Không thể tải thông tin nhà cung cấp');
            }
        };

        fetchSupplier();
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSupplier(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const response = await axios.put(`/api/suppliers/${id}`, supplier);
            setLoading(false);
            history.push('/suppliers'); // Chuyển hướng về danh sách nhà cung cấp sau khi cập nhật thành công
        } catch (err) {
            setLoading(false);
            setError('Có lỗi xảy ra khi cập nhật nhà cung cấp');
        }
    };

    return (
        <div className="update-supplier">
            <h2>Cập nhật nhà cung cấp</h2>
            {error && <div className="error-message">{error}</div>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="name">Tên nhà cung cấp</label>
                    <input 
                        type="text"
                        id="name"
                        name="name"
                        value={supplier.name}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div>
                    <label htmlFor="email">Email</label>
                    <input 
                        type="email"
                        id="email"
                        name="email"
                        value={supplier.email}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div>
                    <label htmlFor="phone">Số điện thoại</label>
                    <input 
                        type="text"
                        id="phone"
                        name="phone"
                        value={supplier.phone}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div>
                    <label htmlFor="address">Địa chỉ</label>
                    <input 
                        type="text"
                        id="address"
                        name="address"
                        value={supplier.address}
                        onChange={handleChange}
                        required
                    />
                </div>

                <div>
                    <label htmlFor="productsSupplied">Sản phẩm cung cấp (ID sản phẩm)</label>
                    <input 
                        type="text"
                        id="productsSupplied"
                        name="productsSupplied"
                        value={supplier.productsSupplied.join(', ')} // Chuyển mảng thành chuỗi để hiển thị
                        onChange={handleChange}
                    />
                    <small>Nhập ID sản phẩm, cách nhau bằng dấu phẩy.</small>
                </div>

                <button type="submit" disabled={loading}>
                    {loading ? 'Đang cập nhật...' : 'Cập nhật'}
                </button>
            </form>
        </div>
    );
};

export default UpdateSupplier;
