import Chart from "../../components/admin/Chart";
import { useEffect, useMemo, useState } from "react";
import axios from 'axios';

export default function ChartIncome() {
    const [userStats, setUserStats] = useState([]);
    const [weeklyStats, setWeeklyStats] = useState([]);

    const MONTHS = useMemo(
        () => [
            "Tháng 1",
            "Tháng 2",
            "Tháng 3",
            "Tháng 4",
            "Tháng 5",
            "Tháng 6",
            "Tháng 7",
            "Tháng 8",
            "Tháng 9",
            "Tháng 10",
            "Tháng 11",
            "Tháng 12",
        ],
        []
    );

    const WEEKS = useMemo(
        () => ["Tuần 1", "Tuần 2", "Tuần 3", "Tuần 4", "Tuần 5"],
        []
    );

    useEffect(() => {
        const getStats = async () => {
            try {
                // Lấy doanh thu theo tháng
                const monthlyRes = await axios.get('/api/v1/admin/orders/income');
                const monthlyStats = MONTHS.map((month, index) => {
                    const monthData = monthlyRes.data.find(item => item._id === index + 1);
                    return {
                        name: month,
                        "Doanh thu": monthData ? monthData.total : 0,
                    };
                });
                setUserStats(monthlyStats);

                // Lấy doanh thu theo tuần
                const weeklyRes = await axios.get('/api/v1/admin/orders/weekly-income');
                const weeklyStats = WEEKS.map((week, index) => {
                    const weekData = weeklyRes.data.find(item => item.week === index + 1);
                    return {
                        name: week,
                        "Doanh thu": weekData ? weekData.total : 0,
                    };
                });
                setWeeklyStats(weeklyStats);

            } catch (error) {
                console.error(error);
            }
        };

        getStats();
    }, [MONTHS, WEEKS]);

    return (
        <div>
            {/* Biểu đồ doanh thu hàng tháng */}
            <Chart
                data={userStats}
                title="Doanh thu hàng tháng"
                grid
                dataKey="Doanh thu"
            />
            {/* Biểu đồ doanh thu theo tuần */}
            <Chart
                data={weeklyStats}
                title="Doanh thu hàng tuần"
                grid
                dataKey="Doanh thu"
            />
        </div>
    );
}
