import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useHistory } from 'react-router-dom';

const UpdateSupplier = () => {
    const { id } = useParams();  // Lấy id nhà cung cấp từ URL
    const history = useHistory(); // Để chuyển hướng người dùng sau khi cập nhật thành công
    const [supplier, setSupplier] = useState({
        name: '',
        email: '',
        phone: '',
        address: '',
        productsSupplied: []
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    useEffect(() => {
        // Tải thông tin nhà cung cấp khi component được render
        const fetchSupplier = async () => {
            try {
                setLoading(true);
                const { data } = await axios.get(`/api/suppliers/${id}`);
                setSupplier(data.supplier);  // Cập nhật state với thông tin nhà cung cấp
                setLoading(false);
            } catch (err) {
                setLoading(false);
                setError('Không thể tải thông tin nhà cung cấp');
            }
        };

        fetchSupplier();
    }, [id]); // Chạy lại khi id thay đổi

    const handleChange = (e) => {
        const { name, value } = e.target;
        setSupplier(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            setLoading(true);
            await axios.put(`/api/suppliers/${id}`, supplier);  // Gửi yêu cầu cập nhật thông tin nhà cung cấp
            setLoading(false);
            history.push('/admin/suppliers');  // Chuyển hướng về danh sách nhà cung cấp sau khi cập nhật thành công
        } catch (err) {
            setLoading(false);
            setError('Có lỗi khi cập nhật nhà cung cấp');
        }
    };

    if (loading) return <div>Đang tải...</div>;

    return (
        <div>
            <h1>Chỉnh sửa Nhà Cung Cấp</h1>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="name">Tên Nhà Cung Cấp</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={supplier.name || ''}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="email">Email</label>
                    <input
                        type="email"
                        id="email"
                        name="email"
                        value={supplier.email || ''}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="phone">Số điện thoại</label>
                    <input
                        type="text"
                        id="phone"
                        name="phone"
                        value={supplier.phone || ''}
                        onChange={handleChange}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="address">Địa chỉ</label>
                    <input
                        type="text"
                        id="address"
                        name="address"
                        value={supplier.address || ''}
                        onChange={handleChange}
                        required
                    />
                </div>
                <button type="submit" disabled={loading}>
                    {loading ? 'Đang cập nhật...' : 'Cập nhật'}
                </button>
            </form>
        </div>
    );
};

export default UpdateSupplier;
