import {
    GET_SUPPLIERS_REQUEST,
    GET_SUPPLIERS_SUCCESS,
    GET_SUPPLIERS_FAIL,
    GET_SUPPLIER_DETAILS_REQUEST,
    GET_SUPPLIER_DETAILS_SUCCESS,
    GET_SUPPLIER_DETAILS_FAIL,
    CREATE_SUPPLIER_REQUEST,
    CREATE_SUPPLIER_SUCCESS,
    CREATE_SUPPLIER_FAIL,
    DELETE_SUPPLIER_REQUEST,
    DELETE_SUPPLIER_SUCCESS,
    DELETE_SUPPLIER_FAIL,
    UPDATE_SUPPLIER_REQUEST,
    UPDATE_SUPPLIER_SUCCESS,
    UPDATE_SUPPLIER_FAIL,
} from '../constants/supplierConstants';

// src/reducers/supplierReducers.js

const initialState = {
    supplier: {}, // Giá trị mặc định cho supplier
    loading: false,
    error: null,
};

export const supplierDetailsReducer = (state = initialState, action) => {
    switch (action.type) {
        case GET_SUPPLIER_DETAILS_REQUEST:
            return { ...state, loading: true };
        case GET_SUPPLIER_DETAILS_SUCCESS:
            return { loading: false, supplier: action.payload };
        case GET_SUPPLIER_DETAILS_FAIL:
            return { loading: false, error: action.payload };
        default:
            return state;
    }
};

export const supplierReducer = (state = initialState, action) => {
    switch (action.type) {
        // Các case khác như bạn đã mô tả
        case GET_SUPPLIERS_REQUEST:
            return { ...state, loading: true };

        case GET_SUPPLIERS_SUCCESS:
            return { ...state, loading: false, suppliers: action.payload };

        case GET_SUPPLIERS_FAIL:
            return { ...state, loading: false, error: action.payload };

        case CREATE_SUPPLIER_REQUEST:
            return { ...state, loading: true };

        case CREATE_SUPPLIER_SUCCESS:
            return {
                ...state,
                loading: false,
                suppliers: [...state.suppliers, action.payload],
            };

        case CREATE_SUPPLIER_FAIL:
            return { ...state, loading: false, error: action.payload };

        case DELETE_SUPPLIER_REQUEST:
            return { ...state, loading: true };

        case DELETE_SUPPLIER_SUCCESS:
            return {
                ...state,
                loading: false,
                suppliers: state.suppliers.filter(
                    (supplier) => supplier._id !== action.payload
                ),
            };

        case DELETE_SUPPLIER_FAIL:
            return { ...state, loading: false, error: action.payload };

        case UPDATE_SUPPLIER_REQUEST:
            return { ...state, loading: true };

        case UPDATE_SUPPLIER_SUCCESS:
            return {
                ...state,
                loading: false,
                suppliers: state.suppliers.map((supplier) =>
                    supplier._id === action.payload._id ? action.payload : supplier
                ),
            };

        case UPDATE_SUPPLIER_FAIL:
            return { ...state, loading: false, error: action.payload };

        default:
            return state;
    }
};
