import React, { useState, useEffect, Fragment } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updateSupplier, clearErrors } from '../../actions/supplierActions'; // Thay đổi tên action
import { UPDATE_SUPPLIER_RESET } from '../../constants/supplierConstants';
import { useAlert } from 'react-alert';
import { useHistory, useParams } from 'react-router-dom';
import Sidebar from './Sidebar'; // Import Sidebar

const UpdateSupplier = () => { // Đổi tên từ EditSupplier thành UpdateSupplier
    const { id } = useParams(); // Lấy id nhà cung cấp từ URL
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');
    const [productsSupplied, setProductsSupplied] = useState([]); // Mảng sản phẩm cung cấp

    const dispatch = useDispatch();
    const alert = useAlert();
    const history = useHistory();

    const { error, success, supplier } = useSelector(state => state.suppliers);

    useEffect(() => {
        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }

        if (success) {
            alert.success('Nhà cung cấp đã được cập nhật');
            history.push('/admin/suppliers');
            dispatch({ type: UPDATE_SUPPLIER_RESET });
        }

        // Nếu có nhà cung cấp, cập nhật state
        if (supplier && supplier._id === id) {
            setName(supplier.name);
            setEmail(supplier.email);
            setPhone(supplier.phone);
            setAddress(supplier.address);
            setProductsSupplied(supplier.productsSupplied.map(product => product._id));
        }
    }, [dispatch, alert, error, success, history, id, supplier]);

    const handleSubmit = (e) => {
        e.preventDefault();
    
        const supplierData = {
            name,
            email,
            phone,
            address,
            productsSupplied // Mảng sản phẩm cung cấp
        };
    
        dispatch(updateSupplier(id, supplierData)); // Gửi dữ liệu tới action
    };

    return (
        <Fragment>
            <div className="row">
                <div className="col-12 col-md-2">
                    <Sidebar />
                </div>

                <div className="col-12 col-md-10">
                    <div className="container">
                        <h1 className="my-5">Cập nhật nhà cung cấp</h1>

                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label htmlFor="name">Tên nhà cung cấp</label>
                                <input 
                                    type="text" 
                                    id="name" 
                                    className="form-control" 
                                    value={name}
                                    onChange={(e) => setName(e.target.value)} 
                                    required 
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="email">Email</label>
                                <input 
                                    type="email" 
                                    id="email" 
                                    className="form-control" 
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)} 
                                    required 
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="phone">Số điện thoại</label>
                                <input 
                                    type="text" 
                                    id="phone" 
                                    className="form-control" 
                                    value={phone}
                                    onChange={(e) => setPhone(e.target.value)} 
                                    required 
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="address">Địa chỉ</label>
                                <textarea 
                                    id="address" 
                                    className="form-control" 
                                    value={address}
                                    onChange={(e) => setAddress(e.target.value)} 
                                    required 
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="productsSupplied">Sản phẩm cung cấp</label>
                                <select
                                    id="productsSupplied"
                                    className="form-control"
                                    multiple
                                    value={productsSupplied}
                                    onChange={(e) => setProductsSupplied(Array.from(e.target.selectedOptions, option => option.value))}
                                    required
                                >
                                    <option value="product1">Sản phẩm 1</option>
                                    <option value="product2">Sản phẩm 2</option>
                                    <option value="product3">Sản phẩm 3</option>
                                </select>
                            </div>

                            <button type="submit" className="btn btn-primary mt-3">Cập nhật nhà cung cấp</button>
                        </form>
                    </div>
                </div>
            </div>
        </Fragment>
    );
};

export default UpdateSupplier;
