import React, { useState, useEffect, Fragment } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updateSupplier, fetchSupplierDetail, clearErrors } from '../../actions/supplierActions';
import { UPDATE_SUPPLIER_RESET } from '../../constants/supplierConstants';
import { useAlert } from 'react-alert';
import { useHistory, useParams } from 'react-router-dom';
import Sidebar from './Sidebar'; // Import Sidebar
import axios from 'axios'; // Dùng để lấy danh sách sản phẩm

const EditSupplier = () => {
    const { id } = useParams(); // Lấy id nhà cung cấp từ URL
    const dispatch = useDispatch();
    const alert = useAlert();
    const history = useHistory();

    // State cho thông tin nhà cung cấp và sản phẩm
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');
    const [productsSupplied, setProductsSupplied] = useState([]); // Mảng ID sản phẩm cung cấp
    const [products, setProducts] = useState([]); // Danh sách sản phẩm

    // Redux State
    const { supplier, loading, error } = useSelector((state) => state.supplierDetails || {});
    const { success: updateSuccess, error: updateError } = useSelector((state) => state.updateSupplier || {});

    // Load thông tin nhà cung cấp
    useEffect(() => {
        if (updateError) {
            alert.error(updateError);
            dispatch(clearErrors());
        }
        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }
        if (updateSuccess) {
            alert.success('Cập nhật nhà cung cấp thành công');
            dispatch({ type: UPDATE_SUPPLIER_RESET });
            history.push('/admin/suppliers');
        }

        if (!supplier.name || supplier._id !== id) {
            dispatch(fetchSupplierDetail(id)); // Lấy thông tin nhà cung cấp từ server
        } else {
            setName(supplier.name);
            setEmail(supplier.email);
            setPhone(supplier.phone);
            setAddress(supplier.address);
            setProductsSupplied(supplier.productsSupplied);
        }

        // Lấy danh sách sản phẩm
        const fetchProducts = async () => {
            try {
                const { data } = await axios.get('/api/products'); // Thay đổi endpoint nếu cần
                setProducts(data.products);
            } catch (error) {
                alert.error('Không thể lấy danh sách sản phẩm');
            }
        };

        fetchProducts();
    }, [dispatch, id, supplier, updateSuccess, error, updateError, alert, history]);

    // Xử lý sự kiện thay đổi input
    const handleChange = (e) => {
        const { name, value } = e.target;
        if (name === 'name') setName(value);
        if (name === 'email') setEmail(value);
        if (name === 'phone') setPhone(value);
        if (name === 'address') setAddress(value);
        if (name === 'productsSupplied') setProductsSupplied(value);
    };

    // Xử lý submit form
    const handleSubmit = (e) => {
        e.preventDefault();
        const supplierData = {
            name,
            email,
            phone,
            address,
            productsSupplied
        };

        dispatch(updateSupplier(id, supplierData));
    };

    return (
        <Fragment>
            <Sidebar />
            <div className="container">
                <h1>Edit Supplier</h1>
                {loading ? (
                    <p>Loading...</p>
                ) : (
                    <form onSubmit={handleSubmit}>
                        <div className="form-group">
                            <label htmlFor="name">Tên nhà cung cấp</label>
                            <input
                                type="text"
                                id="name"
                                name="name"
                                value={name}
                                onChange={handleChange}
                                className="form-control"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="email">Email</label>
                            <input
                                type="email"
                                id="email"
                                name="email"
                                value={email}
                                onChange={handleChange}
                                className="form-control"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="phone">Số điện thoại</label>
                            <input
                                type="text"
                                id="phone"
                                name="phone"
                                value={phone}
                                onChange={handleChange}
                                className="form-control"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="address">Địa chỉ</label>
                            <input
                                type="text"
                                id="address"
                                name="address"
                                value={address}
                                onChange={handleChange}
                                className="form-control"
                                required
                            />
                        </div>

                        <div className="form-group">
                            <label htmlFor="productsSupplied">Sản phẩm cung cấp</label>
                            <select
                                multiple
                                id="productsSupplied"
                                name="productsSupplied"
                                value={productsSupplied}
                                onChange={handleChange}
                                className="form-control"
                            >
                                {products.map((product) => (
                                    <option key={product._id} value={product._id}>
                                        {product.name}
                                    </option>
                                ))}
                            </select>
                        </div>

                        <button type="submit" className="btn btn-primary">
                            Cập nhật nhà cung cấp
                        </button>
                    </form>
                )}
            </div>
        </Fragment>
    );
};

export default EditSupplier;
