import React, { Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { MDBDataTable } from 'mdbreact';

import MetaData from '../layout/MetaData';
import Loader from '../layout/Loader';
import Sidebar from './Sidebar';

import { useAlert } from 'react-alert';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSuppliers, deleteSupplier, clearErrors } from '../../actions/supplierActions';
import { DELETE_SUPPLIER_RESET } from '../../constants/supplierConstants';

const SupplierList = ({ history }) => {

    const alert = useAlert();
    const dispatch = useDispatch();

    // Lấy dữ liệu từ redux state
    const { loading, error, suppliers } = useSelector(state => state.suppliers);
    const { isDeleted } = useSelector(state => state.suppliers);

    useEffect(() => {
        dispatch(fetchSuppliers());

        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }

        if (isDeleted) {
            alert.success('Nhà cung cấp đã bị xóa thành công');
            history.push('/admin/suppliers');
            dispatch({ type: DELETE_SUPPLIER_RESET });
        }

    }, [dispatch, alert, error, isDeleted, history]);

    const deleteHandler = (id) => {
        dispatch(deleteSupplier(id));
    };

    const setSuppliers = () => {
        const data = {
            columns: [
                {
                    label: 'ID',
                    field: 'id',
                    sort: 'asc',
                },
                {
                    label: 'Tên nhà cung cấp',
                    field: 'name',
                    sort: 'asc',
                },
                {
                    label: 'Email',
                    field: 'email',
                    sort: 'asc',
                },
                {
                    label: 'Số điện thoại',
                    field: 'phone',
                    sort: 'asc',
                },
                {
                    label: 'Hành động',
                    field: 'actions',
                }
            ],
            rows: suppliers.map(supplier => ({
                id: supplier._id,
                name: supplier.name,
                email: supplier.email,
                phone: supplier.phone,
                actions: (
                    <Fragment>
                        <Link to={`/admin/supplier/${supplier._id}`} className="btn btn-primary py-1 px-2 mr-2">
                            Sửa
                        </Link>
                        <button
                            className="btn btn-danger py-1 px-2"
                            onClick={() => deleteHandler(supplier._id)}
                        >
                            Xóa
                        </button>
                    </Fragment>
                ),
            }))
        };
        return data;
    };

    return (
        <Fragment>
            <MetaData title={'Danh sách nhà cung cấp'} />
            <div className="row">
                <div className="col-12 col-md-2">
                    <Sidebar />
                </div>
                <div className="col-12 col-md-10">
                    <Fragment>
                        <h1 className="my-5">Danh sách nhà cung cấp</h1>
                        {loading ? <Loader /> : (
                            <MDBDataTable
                                data={setSuppliers()}
                                bordered
                                striped
                                hover
                                searchTop
                                searchBottom={false}
                            />
                        )}
                    </Fragment>
                </div>
            </div>
        </Fragment>
    );
};

export default SupplierList;
