import React, { useState, useEffect, Fragment } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updateSupplier, fetchSupplierDetail, clearErrors } from '../../actions/supplierActions';
import { UPDATE_SUPPLIER_RESET } from '../../constants/supplierConstants';
import { useAlert } from 'react-alert';
import { useHistory, useParams } from 'react-router-dom';
import Sidebar from './Sidebar'; // Import Sidebar

const EditSupplier = () => {
    const { id } = useParams(); // Lấy id nhà cung cấp từ URL
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');
    const [productsSupplied, setProductsSupplied] = useState([]); // Mảng ID sản phẩm cung cấp
    const [products, setProducts] = useState([]); // Danh sách sản phẩm

    const dispatch = useDispatch();
    const { supplier, loading, error } = useSelector((state) => state.supplierDetails);

    const alert = useAlert();
    const history = useHistory();

    useEffect(() => {
        if (id) {
            dispatch(fetchSupplierDetail(id)); // Fetch thông tin nhà cung cấp khi component mount
        }

        if (error) {
            alert.error(error); // Hiển thị lỗi nếu có
            dispatch(clearErrors()); // Xóa lỗi sau khi hiển thị
        }

    }, [dispatch, id, error, alert]);

    useEffect(() => {
        if (supplier) {
            setName(supplier.name);
            setEmail(supplier.email);
            setPhone(supplier.phone);
            setAddress(supplier.address);
            setProductsSupplied(supplier.productsSupplied || []);
        }
    }, [supplier]); // Cập nhật state khi supplier thay đổi

    const handleSubmit = (e) => {
        e.preventDefault();

        const updatedSupplier = {
            name,
            email,
            phone,
            address,
            productsSupplied
        };

        dispatch(updateSupplier(id, updatedSupplier)); // Gửi thông tin cập nhật

        // Khi cập nhật thành công, reset hoặc redirect đến trang khác nếu cần
        dispatch({ type: UPDATE_SUPPLIER_RESET });
    };

    return (
        <Fragment>
            <Sidebar />
            <div className="edit-supplier-form">
                <h1>Edit Supplier</h1>
                {loading ? <p>Loading...</p> : (
                    <form onSubmit={handleSubmit}>
                        <div>
                            <label>Name</label>
                            <input
                                type="text"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>
                        <div>
                            <label>Email</label>
                            <input
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                        </div>
                        <div>
                            <label>Phone</label>
                            <input
                                type="text"
                                value={phone}
                                onChange={(e) => setPhone(e.target.value)}
                            />
                        </div>
                        <div>
                            <label>Address</label>
                            <input
                                type="text"
                                value={address}
                                onChange={(e) => setAddress(e.target.value)}
                            />
                        </div>
                        <div>
                            <label>Products Supplied</label>
                            <input
                                type="text"
                                value={productsSupplied.join(', ')}
                                onChange={(e) => setProductsSupplied(e.target.value.split(', '))}
                            />
                        </div>
                        <button type="submit">Update Supplier</button>
                    </form>
                )}
            </div>
        </Fragment>
    );
};

export default EditSupplier;
