import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { createUser, clearErrors } from '../../actions/userActions';
import { useAlert } from 'react-alert';
import { USER_CREATE_RESET } from '../../constants/userConstants';
import Sidebar from './Sidebar'; // Import Sidebar

const NewUser = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('user'); // Default là 'user'
    const [avatar, setAvatar] = useState(null); // Thêm state cho Hình đại diện

    const dispatch = useDispatch();

    const userCreate = useSelector((state) => state.userCreate);
    const { loading, error, success } = userCreate;

    const submitHandler = (e) => {
        e.preventDefault();

        // Tạo formData để gửi avatar cùng các trường khác
        const formData = new FormData();
        formData.append('name', name);
        formData.append('email', email);
        formData.append('password', password);
        formData.append('role', role);
        formData.append('avatar', avatar);

        dispatch(createUser(formData));
    };

    if (success) {
        alert('Người dùng đã được tạo thành công!');
        dispatch({ type: USER_CREATE_RESET });
        setName('');
        setEmail('');
        setPassword('');
        setRole('user');
        setAvatar(null);
    }

    return (
        <div className="create-user-container">
            <h2>Tạo Người Dùng Mới</h2>
            {loading && <p>Đang xử lý...</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={submitHandler} encType="multipart/form-data">
                <div>
                    <label htmlFor="name">Tên:</label>
                    <input
                        type="text"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="email">Email:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="password">Mật khẩu:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                <div>
                    <label htmlFor="role">Vai trò:</label>
                    <select
                        id="role"
                        value={role}
                        onChange={(e) => setRole(e.target.value)}
                    >
                        <option value="user">Người dùng</option>
                        <option value="admin">Quản trị viên</option>
                        <option value="staff">Nhân viên</option>
                    </select>
                </div>
                <div>
                    <label htmlFor="avatar">Hình đại diện:</label>
                    <input
                        type="file"
                        id="avatar"
                        accept="image/*"
                        onChange={(e) => setAvatar(e.target.files[0])}
                        required
                    />
                </div>
                <button type="submit">Tạo</button>
            </form>
        </div>
    );
};

export default NewUser;
