import React, { useState, useEffect, Fragment } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { newProduct, clearErrors } from '../../actions/productActions';
import { NEW_PRODUCT_RESET } from '../../constants/productConstants';
import { useAlert } from 'react-alert';
import { useHistory } from 'react-router-dom';
import Sidebar from './Sidebar';
import axios from 'axios'; // Dùng để lấy danh sách danh mục

const NewProduct = () => {
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [stock, setStock] = useState('');
    const [category, setCategory] = useState('');
    const [categories, setCategories] = useState([]); // Danh sách các danh mục

    const dispatch = useDispatch();
    const alert = useAlert();
    const history = useHistory();

    const { error, success } = useSelector(state => state.products);

    useEffect(() => {
        // Lấy danh sách danh mục từ API
        const fetchCategories = async () => {
            try {
                const response = await axios.get('/api/v1/categories'); // Giả sử bạn có API này
                setCategories(response.data.categories);
            } catch (err) {
                alert.error('Không thể lấy danh sách danh mục');
            }
        };

        fetchCategories();

        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }

        if (success) {
            alert.success('Sản phẩm đã được thêm mới');
            history.push('/admin/products');
            dispatch({ type: NEW_PRODUCT_RESET });
        }
    }, [dispatch, alert, error, success, history]);

    const handleSubmit = (e) => {
        e.preventDefault();

        const productData = {
            name,
            description,
            price,
            stock,
            category // Danh mục của sản phẩm
        };

        console.log(productData); // Kiểm tra dữ liệu
        dispatch(newProduct(productData)); // Gửi dữ liệu tới action
    };

    return (
        <Fragment>
            <div className="row">
                <div className="col-12 col-md-2">
                    <Sidebar />
                </div>

                <div className="col-12 col-md-10">
                    <div className="container">
                        <h1 className="my-5">Thêm mới sản phẩm</h1>

                        <form onSubmit={handleSubmit}>
                            <div className="form-group">
                                <label htmlFor="name">Tên sản phẩm</label>
                                <input 
                                    type="text" 
                                    id="name" 
                                    className="form-control" 
                                    value={name}
                                    onChange={(e) => setName(e.target.value)} 
                                    required 
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="description">Mô tả</label>
                                <textarea 
                                    id="description" 
                                    className="form-control" 
                                    value={description}
                                    onChange={(e) => setDescription(e.target.value)} 
                                    required 
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="price">Giá</label>
                                <input 
                                    type="number" 
                                    id="price" 
                                    className="form-control" 
                                    value={price}
                                    onChange={(e) => setPrice(e.target.value)} 
                                    required 
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="stock">Số lượng</label>
                                <input 
                                    type="number" 
                                    id="stock" 
                                    className="form-control" 
                                    value={stock}
                                    onChange={(e) => setStock(e.target.value)} 
                                    required 
                                />
                            </div>

                            <div className="form-group">
                                <label htmlFor="category">Danh mục</label>
                                <select
                                    id="category"
                                    className="form-control"
                                    value={category}
                                    onChange={(e) => setCategory(e.target.value)}
                                    required
                                >
                                    <option value="">Chọn danh mục</option>
                                    {categories.map(cat => (
                                        <option key={cat._id} value={cat._id}>
                                            {cat.name}
                                        </option>
                                    ))}
                                </select>
                            </div>

                            <button type="submit" className="btn btn-primary mt-3">Lưu sản phẩm</button>
                        </form>
                    </div>
                </div>
            </div>
        </Fragment>
    );
};

export default NewProduct;
