import React, { Fragment, useState, useEffect } from 'react';
import MetaData from '../layout/MetaData';
import Sidebar from './Sidebar';
import { useAlert } from 'react-alert';
import { useDispatch, useSelector } from 'react-redux';
import { updateSupplier, fetchSuppliers, clearErrors } from '../../actions/supplierActions';
import { UPDATE_SUPPLIER_RESET } from '../../constants/supplierConstants';

const UpdateSupplier = ({ match, history }) => {
    const [name, setName] = useState('');
    const [contact, setContact] = useState('');
    const [address, setAddress] = useState('');

    const alert = useAlert();
    const dispatch = useDispatch();

    const { error, supplier } = useSelector(state => state.supplierDetails || {});

    const { loading = false, error: updateError = null, isUpdated = false } = useSelector(state => state.supplier || {});

    const supplierId = match.params.id;

    useEffect(() => {
        if (supplier && supplier._id !== supplierId) {
            dispatch(fetchSuppliers(supplierId));
        } else {
            setName(supplier.name);
            setContact(supplier.contact);
            setAddress(supplier.address);
        }

        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }

        if (updateError) {
            alert.error(updateError);
            dispatch(clearErrors());
        }

        if (isUpdated) {
            history.push('/admin/suppliers');
            alert.success('Supplier updated successfully');
            dispatch({ type: UPDATE_SUPPLIER_RESET });
        }
    }, [dispatch, alert, error, isUpdated, history, updateError, supplier, supplierId]);

    const submitHandler = (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.set('name', name);
        formData.set('contact', contact);
        formData.set('address', address);

        dispatch(updateSupplier(supplier._id, formData));
    };

    return (
        <Fragment>
            <MetaData title={'Update Supplier'} />
            <div className="row">
                <div className="col-12 col-md-2">
                    <Sidebar />
                </div>

                <div className="col-12 col-md-10">
                    <Fragment>
                        <div className="wrapper my-5">
                            <form className="shadow-lg" onSubmit={submitHandler}>
                                <h1 className="mb-4">Update Supplier</h1>

                                <div className="form-group">
                                    <label htmlFor="name_field">Supplier Name</label>
                                    <input
                                        type="text"
                                        id="name_field"
                                        className="form-control"
                                        value={name}
                                        onChange={(e) => setName(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="contact_field">Contact</label>
                                    <input
                                        type="text"
                                        id="contact_field"
                                        className="form-control"
                                        value={contact}
                                        onChange={(e) => setContact(e.target.value)}
                                    />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="address_field">Address</label>
                                    <input
                                        type="text"
                                        id="address_field"
                                        className="form-control"
                                        value={address}
                                        onChange={(e) => setAddress(e.target.value)}
                                    />
                                </div>

                                <button
                                    id="submit_button"
                                    type="submit"
                                    className="btn btn-block py-3"
                                    disabled={loading ? true : false}
                                >
                                    Update Supplier
                                </button>
                            </form>
                        </div>
                    </Fragment>
                </div>
            </div>
        </Fragment>
    );
};

export default UpdateSupplier;
