import React, { Fragment, useState, useEffect } from 'react'
import MetaData from '../layout/MetaData'
import Loader from '../layout/Loader'
import { useAlert } from 'react-alert'
import { useDispatch, useSelector } from 'react-redux'
import { register, clearErrors } from '../../actions/userActions'
import Sidebar from './Sidebar'; // Import Sidebar

const Register = ({ history }) => {
    const alert = useAlert();
    const dispatch = useDispatch();

    const { isAuthenticated, error, loading } = useSelector(state => state.auth);

    const [user, setUser] = useState({
        name: '',
        email: '',
        password: '',
    });
    const { name, email, password } = user;

    const [avatar, setAvatar] = useState('');
    const [avatarPreview, setAvatarPreview] = useState('/images/default_avatar.jpg');
    const [formErrors, setFormErrors] = useState({});

    useEffect(() => {
        if (isAuthenticated) {
            history.push('/');
        }

        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }
    }, [dispatch, alert, isAuthenticated, error, history]);

    const validateForm = () => {
        const errors = {};
        if (!name) errors.name = 'Tên không được để trống';
        if (!email || !/\S+@\S+\.\S+/.test(email)) errors.email = 'Email không hợp lệ';
        if (!password) errors.password = 'Mật khẩu không được để trống';

        setFormErrors(errors);
        return Object.keys(errors).length === 0;
    };

    const submitHandler = (e) => {
        e.preventDefault();

        if (validateForm()) {
            const formData = new FormData();
            formData.set('name', name);
            formData.set('email', email);
            formData.set('password', password);

            if (avatar) {
                formData.append('avatar', avatar);
            }

            dispatch(register(formData))
        } else {
            alert.error('Vui lòng kiểm tra lại thông tin!');
        }
    };

    const onChange = (e) => {
        if (e.target.name === 'avatar') {
            const file = e.target.files[0];
            if (!file) return;

            const reader = new FileReader();
            reader.onload = () => {
                setAvatarPreview(reader.result);
            };
            reader.readAsDataURL(file);

            setAvatar(file);
        } else {
            setUser({ ...user, [e.target.name]: e.target.value });
        }
    };

    return (
        <Fragment>
            <MetaData title={'Đăng ký'} />

            <div className="row">
                {/* Sidebar */}
                <div className="col-12 col-md-2">
                    <Sidebar />
                </div>

                {/* Nội dung chính */}
                <div className="col-12 col-md-10">
                    <form className="shadow-lg" onSubmit={submitHandler} encType='multipart/form-data'>
                        <h1 className="mb-3">Đăng ký</h1>

                        <div className="form-group">
                            <label htmlFor="name_field">Tên</label>
                            <input
                                type="text"
                                id="name_field"
                                className="form-control"
                                name='name'
                                value={name}
                                onChange={onChange}
                            />
                            {formErrors.name && <small className="text-danger">{formErrors.name}</small>}
                        </div>

                        <div className="form-group">
                            <label htmlFor="email_field">Email</label>
                            <input
                                type="email"
                                id="email_field"
                                className="form-control"
                                name='email'
                                value={email}
                                onChange={onChange}
                            />
                            {formErrors.email && <small className="text-danger">{formErrors.email}</small>}
                        </div>

                        <div className="form-group">
                            <label htmlFor="password_field">Mật khẩu</label>
                            <input
                                type="password"
                                id="password_field"
                                className="form-control"
                                name='password'
                                value={password}
                                onChange={onChange}
                            />
                            {formErrors.password && <small className="text-danger">{formErrors.password}</small>}
                        </div>

                        <div className='form-group'>
                            <label htmlFor='avatar_upload'>Hình nền</label>
                            <div className='d-flex align-items-center'>
                                <div>
                                    <figure className='avatar mr-3 item-rtl'>
                                        <img
                                            src={avatarPreview}
                                            className='rounded-circle'
                                            alt='Ảnh nền'
                                        />
                                    </figure>
                                </div>
                                <div className='custom-file'>
                                    <input
                                        type='file'
                                        name='avatar'
                                        className='custom-file-input'
                                        id='customFile'
                                        accept="image/*"
                                        onChange={onChange}
                                    />
                                    <label className='custom-file-label' htmlFor='customFile'>
                                        Chọn ảnh
                                    </label>
                                </div>
                            </div>
                        </div>

                        {loading ? <Loader /> : (
                            <button
                                id="register_button"
                                type="submit"
                                className="btn btn-block py-3"
                                disabled={loading ? true : false}
                            >
                                ĐĂNG KÝ
                            </button>
                        )}
                    </form>
                </div>
            </div>

        </Fragment>
    );
};

export default Register;
