import React, { useState, useEffect, Fragment } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { updateSupplier, fetchSupplierDetail, clearErrors } from '../../actions/supplierActions'; // Thay đổi tên action
import { UPDATE_SUPPLIER_RESET } from '../../constants/supplierConstants';
import { useAlert } from 'react-alert';
import { useHistory, useParams } from 'react-router-dom';
import Sidebar from './Sidebar'; // Import Sidebar
import axios from 'axios'; // Dùng để lấy danh sách sản phẩm

const EditSupplier = () => {
    const { id } = useParams(); // Lấy id nhà cung cấp từ URL
    const dispatch = useDispatch();
    const alert = useAlert();
    const history = useHistory();

    // State lưu thông tin nhà cung cấp
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');
    const [productsSupplied, setProductsSupplied] = useState([]); // Mảng ID sản phẩm cung cấp
    const [products, setProducts] = useState([]); // Danh sách sản phẩm để chọn

    // Redux State
    const { supplier = {}, loading, error } = useSelector((state) => state.supplierDetails || {});

    const { success: updateSuccess, error: updateError } = useSelector((state) => state.updateSupplier || {});


    // Lấy thông tin nhà cung cấp khi component mount
    useEffect(() => {
        if (updateError) {
            alert.error(updateError);
            dispatch(clearErrors());
        }
        if (updateSuccess) {
            alert.success('Cập nhật nhà cung cấp thành công');
            dispatch({ type: UPDATE_SUPPLIER_RESET });
            history.push('/admin/suppliers'); // Quay lại danh sách nhà cung cấp sau khi cập nhật
        }

        // Fetch thông tin nhà cung cấp từ API
        if (!supplier || supplier._id !== id) {
            dispatch(fetchSupplierDetail(id));
        } else {
            setName(supplier.name);
            setEmail(supplier.email);
            setPhone(supplier.phone);
            setAddress(supplier.address);
            setProductsSupplied(supplier.productsSupplied);
        }

        // Lấy danh sách sản phẩm từ API
        const fetchProducts = async () => {
            const { data } = await axios.get('/api/products'); // Đảm bảo API này tồn tại
            setProducts(data.products);
        };

        fetchProducts();
    }, [dispatch, id, supplier, alert, history, updateSuccess, updateError]);

    const handleSubmit = (e) => {
        e.preventDefault();

        const updatedSupplierData = {
            name,
            email,
            phone,
            address,
            productsSupplied
        };

        dispatch(updateSupplier(id, updatedSupplierData));
    };

    const handleCheckboxChange = (productId) => {
        if (productsSupplied.includes(productId)) {
            setProductsSupplied(productsSupplied.filter((id) => id !== productId));
        } else {
            setProductsSupplied([...productsSupplied, productId]);
        }
    };

    return (
        <Fragment>
            <Sidebar />
            <div className="content">
                <h1>Cập nhật nhà cung cấp</h1>
                {loading ? (
                    <p>Đang tải...</p>
                ) : (
                    <form onSubmit={handleSubmit}>
                        <div>
                            <label>Tên nhà cung cấp</label>
                            <input
                                type="text"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                                required
                            />
                        </div>
                        <div>
                            <label>Email</label>
                            <input
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </div>
                        <div>
                            <label>Số điện thoại</label>
                            <input
                                type="text"
                                value={phone}
                                onChange={(e) => setPhone(e.target.value)}
                                required
                            />
                        </div>
                        <div>
                            <label>Địa chỉ</label>
                            <input
                                type="text"
                                value={address}
                                onChange={(e) => setAddress(e.target.value)}
                                required
                            />
                        </div>
                        <div>
                            <label>Sản phẩm cung cấp</label>
                            <div>
                                {products.map((product) => (
                                    <div key={product._id}>
                                        <input
                                            type="checkbox"
                                            checked={productsSupplied.includes(product._id)}
                                            onChange={() => handleCheckboxChange(product._id)}
                                        />
                                        <span>{product.name}</span>
                                    </div>
                                ))}
                            </div>
                        </div>
                        <button type="submit">Cập nhật nhà cung cấp</button>
                    </form>
                )}
            </div>
        </Fragment>
    );
};

export default EditSupplier;
