import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSupplierDetail, clearErrors } from '../../actions/supplierActions'; // Sử dụng các action đã khai báo
import { useAlert } from 'react-alert';
import { useParams } from 'react-router-dom';
import Sidebar from './Sidebar';

const EditSupplier = () => {
    const { id } = useParams(); // Lấy id nhà cung cấp từ URL
    const dispatch = useDispatch();
    const alert = useAlert();

    // Các state local cho form
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [phone, setPhone] = useState('');
    const [address, setAddress] = useState('');
    const [productsSupplied, setProductsSupplied] = useState([]);

    // Lấy thông tin nhà cung cấp từ Redux store
    const { supplier, loading, error } = useSelector((state) => state.supplierDetails);

    // Fetch thông tin nhà cung cấp khi component mount
    useEffect(() => {
        if (id) {
            dispatch(fetchSupplierDetail(id)); // Gọi action để fetch dữ liệu
        }
    }, [dispatch, id]);

    // Hiển thị lỗi nếu có
    useEffect(() => {
        if (error) {
            alert.error(error);
            dispatch(clearErrors());
        }
    }, [dispatch, error, alert]);

    // Cập nhật form khi supplier data thay đổi
    useEffect(() => {
        if (supplier) {
            setName(supplier.name);
            setEmail(supplier.email);
            setPhone(supplier.phone);
            setAddress(supplier.address);
            setProductsSupplied(supplier.productsSupplied);
        }
    }, [supplier]);

    // Khi đang load dữ liệu, hiển thị loading
    if (loading) {
        return <div>Loading...</div>;
    }

    // Nếu dữ liệu không có, hiển thị thông báo
    if (!supplier) {
        return <div>No supplier data found</div>;
    }

    // Render form chỉnh sửa nhà cung cấp
    return (
        <div>
            <Sidebar />
            <h1>Edit Supplier</h1>
            <form>
                <div>
                    <label>Name</label>
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>
                <div>
                    <label>Email</label>
                    <input
                        type="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                </div>
                <div>
                    <label>Phone</label>
                    <input
                        type="text"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                    />
                </div>
                <div>
                    <label>Address</label>
                    <input
                        type="text"
                        value={address}
                        onChange={(e) => setAddress(e.target.value)}
                    />
                </div>
                {/* Bạn có thể hiển thị sản phẩm cung cấp tại đây */}
                <div>
                    <label>Products Supplied</label>
                    <input
                        type="text"
                        value={productsSupplied.join(', ')} // Hiển thị danh sách sản phẩm
                        readOnly
                    />
                </div>
                <button type="submit">Update Supplier</button>
            </form>
        </div>
    );
};

export default EditSupplier;
